import React from 'react';

const StepName = ({ formData, setFormData, onNext }) => {
  const handleNext = () => {
    if (!formData.firstName || !formData.lastName) {
      alert('Please enter both first and last name');
      return;
    }
    onNext();
  };

  return (
    <div className="min-vh-100 d-flex align-items-center justify-content-center bg-dark text-white">
      <div
        className="card bg-transparent border-0 shadow-lg p-5"
        style={{
          backdropFilter: 'blur(12px)',
          background: 'rgba(255, 255, 255, 0.05)',
          borderRadius: '1.5rem',
          maxWidth: '600px',
          width: '100%',
        }}
      >
        <div className="mb-4 text-center">
          <h2
            className="fw-bold"
            style={{
              fontSize: '2rem',
              background: 'linear-gradient(to right, #00e0ff, #ff6ec4)',
              WebkitBackgroundClip: 'text',
              color: 'transparent',
            }}
          >
            What's your name?
          </h2>
          <p className="text-light small">
            Please enter your full name to begin the rental process
          </p>
        </div>

        <div className="row g-3 mb-4">
          <div className="col">
            <input
              type="text"
              className="form-control form-control-lg bg-light bg-opacity-25 text-white border-0"
              placeholder="First Name"
              value={formData.firstName}
              onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
            />
          </div>
          <div className="col">
            <input
              type="text"
              className="form-control form-control-lg bg-light bg-opacity-25 text-white border-0"
              placeholder="Last Name"
              value={formData.lastName}
              onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
            />
          </div>
        </div>

        <div className="text-end">
          <button
            onClick={handleNext}
            className="btn text-white px-4 py-2"
            style={{
              background: 'linear-gradient(to right, #00e0ff, #ff6ec4)',
              borderRadius: '30px',
            }}
          >
            Next â†’
          </button>
        </div>
      </div>
    </div>
  );
};

export default StepName;
