import React from 'react';

const StepName = ({ formData, setFormData, onNext }) => {
  const handleNext = () => {
    if (!formData.firstName || !formData.lastName) {
      alert('Please enter both first and last name');
      return;
    }
    onNext();
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: 'linear-gradient(to bottom right, #0f2027, #203a43, #2c5364)',
      color: 'white',
      padding: '2rem'
    }}>
      <div style={{
        background: 'rgba(255, 255, 255, 0.05)',
        backdropFilter: 'blur(20px)',
        borderRadius: '20px',
        padding: '2.5rem',
        width: '100%',
        maxWidth: '700px',
        boxShadow: '0 8px 32px rgba(0,0,0,0.2)',
      }}>
        <div className="mb-4 text-center">
          <h2
            className="fw-bold mb-2"
            style={{
              fontSize: '2rem',
              background: 'linear-gradient(to right, #00e0ff, #ff6ec4)',
              WebkitBackgroundClip: 'text',
              color: 'transparent'
            }}
          >
            What's your name?
          </h2>
          <p className="text-light small">
            Please enter your full name to begin the rental process
          </p>
        </div>

        <div className="row g-3 mb-4">
          <div className="col">
            <input
              type="text"
              className="form-control form-control-lg bg-light bg-opacity-10 text-white border border-white border-opacity-10"
              placeholder="First Name"
              value={formData.firstName}
              onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
              style={{ borderRadius: '12px' }}
            />
          </div>
          <div className="col">
            <input
              type="text"
              className="form-control form-control-lg bg-light bg-opacity-10 text-white border border-white border-opacity-10"
              placeholder="Last Name"
              value={formData.lastName}
              onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
              style={{ borderRadius: '12px' }}
            />
          </div>
        </div>

        <div className="text-end">
          <button
            onClick={handleNext}
            className="btn text-white px-4 py-2"
            style={{
              background: 'linear-gradient(to right, #00e0ff, #ff6ec4)',
              borderRadius: '30px',
              border: 'none',
            }}
          >
            Next â†’
          </button>
        </div>
      </div>
    </div>
  );
};

export default StepName;
